import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const QuizResultPage = () => {
  const { id } = useParams();  // Get quiz ID from URL
  const [result, setResult] = useState(null);  // Store result data
  const [loading, setLoading] = useState(true);  // Loading state to display loading message
  const [error, setError] = useState(null);  // Error state to handle any errors

  useEffect(() => {
    const fetchResult = async () => {
      try {
        // Fetch the quiz result from the backend using the quiz ID
        const response = await fetch(`http://localhost:5000/api/results/${id}`);

        if (!response.ok) {
          throw new Error('Failed to fetch result');
        }

        const data = await response.json();
        setResult(data);  // Set the result data
        setLoading(false); // Set loading to false after data is fetched
      } catch (err) {
        setError(err.message);  // Set the error message if something goes wrong
        setLoading(false);
      }
    };

    fetchResult();  // Call the function to fetch the result
  }, [id]);  // Effect runs whenever the `id` changes (when the user navigates to a new quiz result page)

  if (loading) {
    return <div>Loading your result...</div>;  // Show loading message until data is fetched
  }

  if (error) {
    return <div>Error: {error}</div>;  // Display error message if there's an error
  }

  if (!result) {
    return <div>No result found for this quiz.</div>;  // Handle case where no result is found
  }

  return (
    <div className="result-container">
      <h1>Quiz Results</h1>
      <p><strong>Quiz Title:</strong> {result.quiz_title}</p>
      <p><strong>Your Score:</strong> {result.score}</p>
      <p><strong>Submitted By:</strong> {result.user_name}</p>
      {/* You can display other information like feedback, total number of questions, etc. here */}
      <button onClick={() => window.history.back()}>Go Back</button>
    </div>
  );
};

export default QuizResultPage;
