const express = require('express');
const { verifyOtp } = require('../controllers/authController'); // Import OTP verification function
const { getQuizzes, submitQuiz } = require('../controllers/quizController'); // Import quiz fetching and submitting functions
const router = express.Router();

// Route to send OTP (you might already have this implemented elsewhere)
router.post('/send-otp', sendOtp);

// Route to verify OTP and fetch quizzes
router.post('/verify-otp', verifyOtp);

// Route to fetch quizzes (no session required here)
router.get('/quizzes', getQuizzes);

// New route to handle quiz submission
router.post('/submit-quiz', submitQuiz); // New route to handle quiz submissions

module.exports = router;
