// src/pages/QuizPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // To get the quiz ID from the URL
import { getQuizDetails } from '../api'; // Assuming this is the correct API call for quiz details

const QuizPage = () => {
  const { id } = useParams(); // Get quizId from URL
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);

  // Fetch quiz details (questions and answers)
  useEffect(() => {
    const fetchQuizDetails = async () => {
      try {
        const quizData = await getQuizDetails(id);
        setQuiz(quizData); // Store quiz details
      } catch (error) {
        console.error('Error fetching quiz details:', error);
      }
    };

    fetchQuizDetails();
  }, [id]);

  const handleAnswerChange = (questionId, selectedAnswerId) => {
    // Update the selected answer for a given question
    setAnswers((prevAnswers) => {
      const updatedAnswers = prevAnswers.filter(answer => answer.questionId !== questionId);
      updatedAnswers.push({ questionId, answerId: selectedAnswerId });
      return updatedAnswers;
    });
  };

  const handleSubmit = async () => {
    const result = {
      userId: 1,  // Assuming this is the logged-in user's ID (modify accordingly)
      otp: '1234',  // Assuming OTP is already validated (replace with actual OTP handling)
      quizId: id,
      answers: answers.map((answer) => ({
        questionId: answer.questionId,
        isCorrect: answer.answerId === 2, // Example: hardcoded answer check (this should be dynamic)
      })),
    };

    try {
      const response = await fetch('http://localhost:5000/api/submit-quiz', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(result),
      });

      const data = await response.json();
      console.log('Quiz submitted successfully:', data);
      alert(`Your score: ${data.score}`); // Display score upon success
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  if (!quiz) return <div>Loading quiz...</div>;

  return (
    <div>
      <h1>{quiz.title}</h1>
      {quiz.questions.map((question) => (
        <div key={question.id}>
          <h3>{question.question_text}</h3>
          {question.answers.map((answer) => (
            <div key={answer.id}>
              <input
                type="radio"
                name={`question-${question.id}`}
                value={answer.id}
                onChange={() => handleAnswerChange(question.id, answer.id)}
              />
              <label>{answer.answer_text}</label>
            </div>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit Quiz</button>
    </div>
  );
};

export default QuizPage;
