import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import { sendOtp, verifyOtp } from '../services/authService'; 

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const navigate = useNavigate();

  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email);
      setIsOtpSent(true);
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  const handleVerifyOtp = async () => {
    console.log('handleVerifyOtp called');
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      console.log('Verifying OTP...');  // Debugging log
      const isVerified = await verifyOtp(email, otp);
      console.log('OTP verification result:', isVerified);  // Debugging log

      if (isVerified) {
        console.log('OTP verified successfully, navigating to /home');
        navigate('/home'); // Check if navigate is being triggered
      } else {
        console.log('OTP verification failed');
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      console.log('Error during OTP verification:', error);  // Debugging log
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>
 

      {isOtpSent && (
        <div className="otp-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
