import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import { sendOtp, verifyOtp } from '../services/authService'; 

const LoginPage = () => {
  const [email, setEmail] = useState(''); // Allow user to input their email
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false); 
  const navigate = useNavigate(); 

  // Handle sending OTP
  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email); 
      setIsOtpSent(true); 
      setErrorMessage(''); 
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  // Handle OTP verification
  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      const isVerified = await verifyOtp(email, otp); 
      if (isVerified) {
        console.log('OTP verified successfully, navigating to HomePage...');
        navigate('/home'); 
      } else {
        console.log('Invalid OTP, setting error message.');
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      console.error('Error during OTP verification:', error);
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      

      {isOtpSent && (
        <div className="otp-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)} // Update OTP state
            />
          </div>
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
