import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './../../src/App.css';

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL parameter
  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track the current question index
  const [selectedAnswers, setSelectedAnswers] = useState({}); // Track answers for each question
  const [marks, setMarks] = useState(0); // Track total marks
  const [timer, setTimer] = useState(30); // Timer for each question
  const navigate = useNavigate(); // Initialize navigate

  // Retrieve session_id and userId from local storage
  const session_id = localStorage.getItem('session_id');
  const userId = localStorage.getItem('userId');

  // Validate session_id and userId before proceeding
  useEffect(() => {
    if (!session_id || !userId) {
      alert('Session or User ID not found. Please log in again.');
      navigate('/');  // Redirect to login page if session/userId is missing
    }
  }, [session_id, userId, navigate]);

  // Fetch quiz data on page load
  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/quiz/${id}`);
        const data = await response.json();
        console.log('Quiz Data:', data); // Log the data to see what is returned
        setQuiz(data);
      } catch (error) {
        console.error('Error fetching quiz data:', error);
      }
    };

    fetchQuizData();
  }, [id]);

  // Memoize the checkAnswer function to prevent it from being recreated on every render
  const checkAnswer = useCallback((answerId) => {
    const question = quiz?.questions[currentQuestionIndex];
    const correctAnswer = question?.answer_options.find((answer) => answer.is_correct);

    // If the answer is correct, increment marks
    if (correctAnswer?.id === answerId) {
      setMarks((prevMarks) => prevMarks + 1);
    }
  }, [currentQuestionIndex, quiz]);

  // Function to get all answers selected by the user in the required format
  const getAnswers = () => {
    // Log the selected answers when the user clicks next or submit
    console.log('Selected Answers:', selectedAnswers);
    
    return quiz?.questions.map((question) => ({
      questionId: question.id,
      selectedOption: selectedAnswers[question.id],  // Get the selected answer for each question
    }));
  };
  
  //console.log(userId, session_id)

  // Handle answer click
  const handleAnswerClick = (answerText, questionId) => {
    setSelectedAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: answerText,  // Store answer by question ID
    }));
    console.log('Selected answer:', answerText);
  };

  // Handle next question
  const handleNextQuestion = useCallback(() => {
    const currentQuestion = quiz?.questions[currentQuestionIndex];
    if (selectedAnswers[currentQuestion.id]) {
      checkAnswer(selectedAnswers[currentQuestion.id]);
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
      setTimer(30); // Reset timer for next question
    }
  }, [currentQuestionIndex, selectedAnswers, quiz, checkAnswer]);

  // Countdown timer for each question
  useEffect(() => {
    if (timer > 0 && currentQuestionIndex < quiz?.questions.length) {
      const timerInterval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    } else if (timer === 0) {
      handleNextQuestion(); // Move to next question when timer expires
    }
  }, [timer, currentQuestionIndex, quiz, handleNextQuestion]);

  // Submit the quiz
  const submitQuiz = async () => {
    const answers = getAnswers();  // Gather the answers in the format [{questionId, selectedOption}]
    
    try {
      // Send the quiz data (quizId and answers) to the backend
      const response = await fetch('http://localhost:5000/api/submit-quiz', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        Authorization: `Bearer ${session_id}`, // Pass session_id in Authorization header
        credentials: 'include',  // To send the session cookie
        body: JSON.stringify({
          quizId: quiz.id,  // Pass the quiz ID from the state
          userId,
          answers: answers,  // Pass the answers array
        }),
      });
  
      const data = await response.json();
    
      if (data.message === 'Quiz submitted successfully') {
        console.log(data);
        console.log(data.score);
        alert(`Your score is: ${data.score}`);  // Display the score
      } else {
        alert('There was an issue with your submission.');
      }
    } catch (error) {
      console.error('Error submitting quiz:', error);
      alert('Error submitting your quiz. Please try again.');
    }
  };

  if (!quiz) {
    return <div>Loading quiz...</div>;
  }

  if (!quiz?.questions || quiz.questions.length === 0) {
    return <div>No questions available for this quiz.</div>;
  }

  const currentQuestion = quiz?.questions[currentQuestionIndex];

  return (
    <div>
      <h1>{quiz.quiz_title}</h1>

      {/* Display Question and Timer */}
      <div>
        <h3>{currentQuestion?.question_text}</h3>
        <div>Time Left: {timer}s</div>

        {/* Display the answer options */}
        <div className="answers">
          {currentQuestion?.answer_options.map((answer) => (
            <div
              key={answer.answer_text}
              className={`answer-option ${selectedAnswers[currentQuestion.id] === answer.answer_text ? 'selected' : ''}`}
              onClick={() => handleAnswerClick(answer.answer_text, currentQuestion.id)} // Handle answer click
            >
              {answer.answer_text}
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div>
        {currentQuestionIndex < quiz.questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={!selectedAnswers[currentQuestion.id]}>
            Next Question
          </button>
        ) : (
          <button onClick={submitQuiz}>Submit Quiz</button>
        )}
      </div>
    </div>
  );
};

export default QuizPage;
