import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import './../../src/App.css';

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL parameter
  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track the current question index
  const [selectedAnswers, setSelectedAnswers] = useState({}); // Store selected answers for all questions
  const [timer, setTimer] = useState(30); // Timer for each question (30 seconds per question)
  const [score, setScore] = useState(0); // Total score
  const [userId, setUserId] = useState(null); // Track the logged-in user (get this from context or auth)
  const [otp, setOtp] = useState(''); // OTP input by user

  // Fetch quiz data on page load
  useEffect(() => {
    const fetchQuizData = async () => {
      const response = await fetch(`http://localhost:5000/api/quiz/${id}`);
      const data = await response.json();
      setQuiz(data);
    };

    fetchQuizData();
  }, [id]);

  // Memoize the checkAnswer function to prevent it from being recreated on every render
  const checkAnswer = useCallback((questionId, answerId) => {
    const question = quiz.questions.find((q) => q.id === questionId);
    const correctAnswer = question.answers.find((answer) => answer.is_correct);

    if (correctAnswer.id === answerId) {
      setScore((prevScore) => prevScore + question.marks);
    }
  }, [quiz]);

  // Handle next question
  const handleNextQuestion = useCallback(() => {
    if (selectedAnswers[currentQuestionIndex] !== undefined) {
      checkAnswer(currentQuestionIndex, selectedAnswers[currentQuestionIndex]); // Check if the selected answer is correct
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1); // Move to next question
      setTimer(30); // Reset timer for the next question
    }
  }, [selectedAnswers, currentQuestionIndex, checkAnswer]);

  // Countdown timer for each question
  useEffect(() => {
    if (timer > 0 && currentQuestionIndex < quiz?.questions?.length) {
      const timerInterval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    } else if (timer === 0) {
      handleNextQuestion(); // Move to next question when timer expires
    }
  }, [timer, currentQuestionIndex, quiz, handleNextQuestion]);

  // Submit quiz to the server
  const submitQuiz = async () => {
    if (!userId || !otp || currentQuestionIndex !== quiz.questions.length) {
      alert('Please complete the quiz before submitting.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/submit-quiz', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, otp, quizId: id, answers: getAnswers() }),
      });
      const data = await response.json();
      alert(`Quiz submitted successfully! Your score: ${score}`);
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  // Get answers in the required format to send to the backend (assuming { questionId, answerId })
  const getAnswers = () => {
    return Object.keys(selectedAnswers).map((questionId) => ({
      questionId: parseInt(questionId),
      answerId: selectedAnswers[questionId],
    }));
  };

  // Show loading indicator while quiz is being fetched
  if (!quiz) {
    return <div>Loading quiz...</div>;
  }

  // Ensure that quiz.questions is defined before rendering
  if (!quiz.questions) {
    return <div>No questions available for this quiz.</div>;
  }

  const currentQuestion = quiz.questions[currentQuestionIndex];

  return (
    <div>
      <h1>{quiz?.quiz_title}</h1>

      {/* Display Question and Timer */}
      <div>
        <h3>{currentQuestion?.question_text}</h3>
        <div>Time Left: {timer}s</div>

        {/* Display the answer options */}
        <div className="answers">
          {currentQuestion?.answers?.map((answer) => (
            <div
              key={answer.id}
              className={`answer-option ${selectedAnswers[currentQuestionIndex] === answer.id ? 'selected' : ''}`}
              onClick={() => {
                setSelectedAnswers((prev) => ({
                  ...prev,
                  [currentQuestionIndex]: answer.id,
                }));
              }} // Handle selecting an answer
            >
              {answer.answer_text}
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div>
        {currentQuestionIndex < quiz.questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={selectedAnswers[currentQuestionIndex] === undefined}>
            Next Question
          </button>
        ) : (
          <button onClick={submitQuiz}>Submit Quiz</button>
        )}
      </div>
    </div>
  );
};

export default QuizPage;
