// src/pages/OtpPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate for navigation
import { sendOtp, verifyOtp } from '../api';  // Import sendOtp and verifyOtp from api.js

const OtpPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();  // Hook for navigation

  const handleSendOtp = async () => {
    try {
      // Send OTP to the user's email
      await sendOtp(email);  // This triggers the OTP sending process
      alert('OTP sent successfully!');
    } catch (error) {
      console.error('Error sending OTP:', error);
    }
  };

  const handleVerifyOtp = async () => {
    try {
      // Verify OTP entered by the user
      const response = await verifyOtp(email, otp);

      if (response.status === 200) {
        // OTP is verified successfully, navigate to the HomePage
        navigate('/home');
      } else {
        alert('Invalid OTP. Please try again.');
      }
    } catch (error) {
      alert('Error verifying OTP. Please try again.');
      console.error('Error verifying OTP:', error);
    }
  };

  return (
    <div>
      <h2>Verify OTP</h2>
      <input
        type="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button class="button1" onClick={handleSendOtp}>Send OTP</button>

      <input
        type="text"
        placeholder="Enter OTP"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
      />
      <button classonClick={handleVerifyOtp}>Verify OTP</button>
    </div>
  );
};

export default OtpPage;
