import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate hook from React Router v7
import { sendOtp, verifyOtp } from '../services/authService'; // Assuming these functions are defined correctly

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const navigate = useNavigate(); // Initialize navigate

  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email);
      setIsOtpSent(true);
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      const isVerified = await verifyOtp(email, otp);

      if (isVerified) {
        navigate('/home'); // Navigate to the Home Page after OTP verification
      } else {
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>


      {/* Show OTP input if OTP is sent */}
      {isOtpSent && (
        <div className="otp-section">
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {/* Error message display */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
