import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom'; 
import { sendOtp, verifyOtp } from '../services/authService'; 

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const navigate = useNavigate();

  // Handle OTP send action
  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email); // Call service to send OTP
      setIsOtpSent(true); // Mark OTP as sent
      setErrorMessage(''); // Clear any previous error messages
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  // Handle OTP verification action
  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      console.log('Verifying OTP...'); // Debug log
      const isVerified = await verifyOtp(email, otp); // Call service to verify OTP
      console.log('OTP verification result:', isVerified); // Debug log

      if (isVerified) {
        // If OTP is verified successfully, navigate to /home
        console.log('OTP verified successfully, navigating to /home');
        navigate('/home'); 
      } else {
        // If OTP is not valid, show an error message and keep the user on the page
        console.log('OTP verification failed');
        setErrorMessage('Wrong OTP, please retry.');
      }
    } catch (error) {
      console.log('Error during OTP verification:', error); // Debug log
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>

      {/* Input for email */}
      <div className="input-group">
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      
      <button onClick={handleSendOtp} disabled={isOtpSent}>
        {isOtpSent ? 'OTP Sent' : 'Send OTP'}
      </button>

      {isOtpSent && (
        <div className="otp-section">
          {/* OTP input and verification button */}
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {/* Display any error messages */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
