// LoginPage.js (React Component)
import React, { useState } from 'react';
import { sendOtp } from '../services/authService';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);

  // Function to send OTP to the provided email
  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email);
      setIsOtpSent(true);
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>

      <div className="input-group">
        <input
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleSendOtp}>Send OTP</button>
      </div>

      {isOtpSent && (
        <div className="otp-section">
          <p>OTP has been sent to your email. Please enter it in the OTP verification section below.</p>
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
