import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './../../src/App.css';

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL parameter
  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track the current question index
  const [selectedAnswer, setSelectedAnswer] = useState(null); // Track selected answer for the current question
  const [timer, setTimer] = useState(30); // Timer for each question (30 seconds per question)
  const [selectedAnswers, setSelectedAnswers] = useState({}); // Track answers for each question
  const [marks, setMarks] = useState(0); // Track total marks
  const navigate = useNavigate(); // Initialize navigate

  // Fetch quiz data on page load
  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/quiz/${id}`);
        const data = await response.json();
        console.log('Quiz Data:', data); // Log the data to see what is returned
        setQuiz(data);
      } catch (error) {
        console.error('Error fetching quiz data:', error);
      }
    };

    fetchQuizData();
  }, [id]);

  // Memoize the checkAnswer function to prevent it from being recreated on every render
  const checkAnswer = useCallback((answerId) => {
    const question = quiz?.questions[currentQuestionIndex];
    const correctAnswer = question?.answer_options.find((answer) => answer.is_correct);

    // Log the session when an answer is selected
    console.log('Session at answer selection:', sessionStorage.getItem('userSession'));  // Assuming you store session info in sessionStorage

    // If the answer is correct, increment marks
    if (correctAnswer?.id === answerId) {
      setMarks((prevMarks) => prevMarks + 1);
    }

    // If the answer is correct, add it to the selected answers
    if (correctAnswer?.id === answerId) {
      setSelectedAnswers((prev) => ({
        ...prev,
        [quiz.questions[currentQuestionIndex].id]: answerId,
      }));
    }
  }, [currentQuestionIndex, quiz]);

  // Memoize the handleNextQuestion function to prevent it from being recreated on every render
  const handleNextQuestion = useCallback(() => {
    if (selectedAnswer !== null) {
      setSelectedAnswers((prev) => ({
        ...prev,
        [quiz.questions[currentQuestionIndex].id]: selectedAnswer,
      })); // Store the selected answer for the current question
      checkAnswer(selectedAnswer); // Check if the selected answer is correct
      setSelectedAnswer(null); // Reset selected answer
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1); // Move to next question
      setTimer(30); // Reset timer for the next question
    }
  }, [selectedAnswer, checkAnswer, quiz, currentQuestionIndex]);

  // Countdown timer for each question
  useEffect(() => {
    if (timer > 0 && currentQuestionIndex < quiz?.questions.length) {
      const timerInterval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    } else if (timer === 0) {
      handleNextQuestion(); // Move to next question when timer expires
    }
  }, [timer, currentQuestionIndex, quiz, handleNextQuestion]);

  // Function to get all answers selected by the user
  const getAnswers = () => {
    return quiz?.questions.map((question) => ({
      questionId: question.id,
      selectedOption: selectedAnswers[question.id], // Get the answer for each question
    }));
  };

  const submitQuiz = async () => {
    const answers = getAnswers(); // Fetch the answers for submission
    const response = await fetch('http://localhost:5000/api/submit-quiz', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include', // This is the key part for sending cookies
      body: JSON.stringify({
        quizId: id, // Send the quiz ID
        answers: getAnswers(), // Ensure you're sending the answers array
      }),
    });

    const data = await response.json();  // Assuming the response is JSON
    if (data.message === 'Unauthorized. Please log in first.') {
      console.log('Please log in again.');
    } else {
      console.log('Score:', data.score);
    }
  };

  if (!quiz) {
    return <div>Loading quiz...</div>;
  }

  if (!quiz?.questions || quiz.questions.length === 0) {
    return <div>No questions available for this quiz.</div>;
  }

  const currentQuestion = quiz?.questions[currentQuestionIndex];

  return (
    <div>
      <h1>{quiz.quiz_title}</h1>

      {/* Display Question and Timer */}
      <div>
        <h3>{currentQuestion?.question_text}</h3>
        <div>Time Left: {timer}s</div>

        {/* Display the answer options */}
        <div className="answers">
          {currentQuestion?.answer_options.map((answer) => (
            <div
              key={answer.answer_text}
              className={`answer-option ${selectedAnswer === answer.answer_text ? 'selected' : ''}`}
              onClick={() => {
                setSelectedAnswer(answer.answer_text); // Update selected answer for current question
                checkAnswer(answer.id);  // Check if selected answer is correct
              }}
            >
              {answer.answer_text}
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div>
        {currentQuestionIndex < quiz.questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={!selectedAnswer}>
            Next Question
          </button>
        ) : (
          <button onClick={submitQuiz}>Submit Quiz</button>
        )}
      </div>
    </div>
  );
};

export default QuizPage;
