import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { sendOtp, verifyOtp } from '../services/authService';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const navigate = useNavigate();

  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email);
      setIsOtpSent(true); // Mark OTP as sent
      setErrorMessage(''); // Clear any previous error messages
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      const isVerified = await verifyOtp(email, otp); // Verify the OTP

      if (isVerified) {
        // Navigate to home page upon successful verification
        navigate('/home');
      } else {
        // Display error if OTP verification fails
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      // Display any error during the verification process
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>

      <div className="input-group">
        <input
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleSendOtp}>Send OTP</button>
      </div>

      {isOtpSent && (
        <div className="otp-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {/* Error message display */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
