// src/pages/QuizPage.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Importing hooks from react-router-dom
import { getQuizDetails } from '../api'; // Importing the API functions
import { submitQuizResult } from '../api'; // Importing submitQuizResult from services

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const navigate = useNavigate(); // Use navigate inside the component

  useEffect(() => {
    async function fetchQuizDetails() {
      const data = await getQuizDetails(id); // Fetch quiz details using quizId from the URL
      setQuiz(data);
    }
    fetchQuizDetails();
  }, [id]); // Re-run the effect when quizId changes

  const handleAnswer = (questionId, answer) => {
    // Update the answer for a particular question
    setAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[questionId] = answer;  // Store answer for the given questionId
      return updatedAnswers;
    });
  };

  const handleSubmit = () => {
    // Create the result object to submit to the backend
    const result = {
      userId: 1,  // Use actual logged-in user's ID
      otp: '123456',  // Use actual OTP from session/context
      quizId: quiz.id,
      answers: answers.map((answer, index) => ({
        questionId: quiz.questions[index].id,
        isCorrect: answer.isCorrect,  // Map selected answers to the result
      })),
    };

    // Submit the quiz result via the API
    submitQuizResult(result);
    
    // Redirect to the result page after successful submission
    navigate('/result');  // Correct way to navigate using useNavigate
  };

  if (!quiz) return <div>Loading...</div>;  // Show loading if quiz data hasn't been fetched yet

  return (
    <div>
      <h1>{quiz.title}</h1>
      <div>
        {quiz.questions.map((question, index) => (
          <div key={question.id}>
            <h3>{question.question_text}</h3>
            {question.answers.map((answer) => (
              <div key={answer.id}>
                <label>
                  <input
                    type="radio"
                    name={`question-${question.id}`}
                    onChange={() => handleAnswer(index, { answerText: answer.answer_text, isCorrect: answer.is_correct })}
                  />
                  {answer.answer_text}
                </label>
              </div>
            ))}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default QuizPage;
