import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import './../../src/App.css';

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL parameter
  const [quiz, setQuiz] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track the current question index
  const [selectedAnswers, setSelectedAnswers] = useState([]); // Track selected answers for all questions
  const [timer, setTimer] = useState(30); // Timer for each question (30 seconds per question)
  const [score, setScore] = useState(0); // Total score
  const [userId, setUserId] = useState(null); // Track the logged-in user
  const [otp, setOtp] = useState(''); // OTP input by user

  // Fetch quiz data on page load
  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/quiz/${id}`);
        const data = await response.json();
        setQuiz(data);
      } catch (error) {
        console.error('Error fetching quiz data:', error);
      }
    };

    fetchQuizData();
  }, [id]);

  // Check if the selected answer is correct for the current question
  const checkAnswer = useCallback(() => {
    const question = quiz?.questions[currentQuestionIndex];
    const selectedAnswer = selectedAnswers[currentQuestionIndex];
    if (question && selectedAnswer !== undefined) {
      const correctAnswer = question.answer_options.find((answer) => answer.is_correct);
      if (correctAnswer?.id === selectedAnswer) {
        setScore((prevScore) => prevScore + question.marks);
      }
    }
  }, [currentQuestionIndex, selectedAnswers, quiz]);

  // Handle next question
  const handleNextQuestion = useCallback(() => {
    checkAnswer(); // Check the selected answer for the current question
    setCurrentQuestionIndex((prevIndex) => prevIndex + 1); // Move to next question
    setTimer(30); // Reset timer for the next question
  }, [checkAnswer]);

  // Countdown timer for each question
  useEffect(() => {
    if (timer > 0 && currentQuestionIndex < quiz?.questions.length) {
      const timerInterval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    } else if (timer === 0) {
      handleNextQuestion(); // Move to next question when timer expires
    }
  }, [timer, currentQuestionIndex, quiz, handleNextQuestion]);

  // Submit the quiz results
  const submitQuiz = async () => {
    if (!userId || !otp || currentQuestionIndex !== quiz?.questions.length) {
      alert('Please complete the quiz before submitting.');
      return;
    }

    const answers = quiz?.questions.map((question, index) => ({
      questionId: question.id,
      selectedOption: selectedAnswers[index], // The selected answer for this question
    }));

    try {
      const response = await fetch('http://localhost:5000/api/submit-quiz', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userId, otp, quizId: id, answers }),
      });
      const data = await response.json();
      console.log(data.message);
      alert(`Your score: ${score}`);
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  if (!quiz) {
    return <div>Loading quiz...</div>;
  }

  if (!quiz?.questions || quiz.questions.length === 0) {
    return <div>No questions available for this quiz.</div>;
  }

  const currentQuestion = quiz?.questions[currentQuestionIndex];

  return (
    <div>
      <h1>{quiz.quiz_title}</h1>

      {/* Display Question and Timer */}
      <div>
        <h3>{currentQuestion?.question_text}</h3>
        <div>Time Left: {timer}s</div>

        {/* Display the answer options */}
        <div className="answers">
          {currentQuestion?.answer_options.map((answer) => (
            <div
              key={answer.id} // Use answer.id as key
              className={`answer-option ${selectedAnswers[currentQuestionIndex] === answer.id ? 'selected' : ''}`} // Apply 'selected' class
              onClick={() => {
                const updatedAnswers = [...selectedAnswers];
                updatedAnswers[currentQuestionIndex] = answer.id; // Update the selected answer for current question
                setSelectedAnswers(updatedAnswers);
              }}
            >
              {answer.answer_text}
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div>
        {currentQuestionIndex < quiz.questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={selectedAnswers[currentQuestionIndex] === undefined}>
            Next Question
          </button>
        ) : (
          <button onClick={submitQuiz}>Submit Quiz</button>
        )}
      </div>
    </div>
  );
};

export default QuizPage;
