import React, { useState } from 'react';

const LoginPage = ({ setIsOtpVerified }) => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [otpStatus, setOtpStatus] = useState('');

  // Function to send OTP to the user's email
  const sendOtp = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email })
      });

      const data = await response.json();
      setOtpStatus(data.message);
    } catch (error) {
      setOtpStatus('Error sending OTP');
    }
  };

  // Function to verify OTP
  const verifyOtp = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp })
      });

      const data = await response.json();
      if (data.message === 'OTP verified successfully. User logged in.') {
        setOtpStatus(data.message);

        // Mark OTP as verified and allow redirection
        setIsOtpVerified(true);
      } else {
        setOtpStatus(data.message);
      }
    } catch (error) {
      setOtpStatus('Invalid OTP');
    }
  };

  return (
    <div>
      <h2>OTP Verification</h2>
      <div>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter email"
        />
        <button onClick={sendOtp}>Send OTP</button>
      </div>
      <div>
        <input
          type="text"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          placeholder="Enter OTP"
        />
        <button onClick={verifyOtp}>Verify OTP</button>
      </div>
      <p>{otpStatus}</p>
    </div>
  );
};

export default LoginPage;
