// src/pages/LoginPage.js
//update
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import { sendOtp, verifyOtp } from '../services/authService'; // Assume these functions are properly defined in authService

const LoginPage = () => {
  const [email, setEmail] = useState(''); // Use email as the unique identifier for login
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false); // Track OTP sent status
  const navigate = useNavigate(); // Initialize useNavigate for page redirection

  // Handle sending OTP
  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      await sendOtp(email); // Call service function to send OTP to the provided email
      setIsOtpSent(true); // Mark OTP as sent
      setErrorMessage(''); // Clear any previous error messages
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  // Handle OTP verification
  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    try {
      const isVerified = await verifyOtp(email, otp); // Call service function to verify OTP
      if (isVerified) {
        // On success, navigate to HomePage or Dashboard
        navigate('/home');
      } else {
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      setErrorMessage('Error during OTP verification. Please try again later.');
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>
      <div className="input-group">
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)} // Update email state on input change
        />
      </div>
      
      <button onClick={handleSendOtp} disabled={isOtpSent}>
        {isOtpSent ? 'OTP Sent' : 'Send OTP'}
      </button>

      {isOtpSent && (
        <div className="otp-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)} // Update OTP state
            />
          </div>
          <button onClick={handleVerifyOtp}>Verify OTP</button>
        </div>
      )}

      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
