// src/pages/OtpPage.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate for navigation
import { verifyOtp } from '.';  // Assuming you have an API call to verify OTP

const OtpPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const navigate = useNavigate();  // Hook for navigation

  const sendOtp = async () => {
    // Send OTP logic to backend (no changes needed)
  };

  const handleVerifyOtp = async () => {
    try {
      // Assuming verifyOtp function makes a POST request to the backend to verify OTP
      const response = await verifyOtp({ email, otp });

      if (response.status === 200) {
        // OTP is verified, now navigate to the HomePage where quizzes are listed
        navigate('/home');
      } else {
        alert('Invalid OTP. Please try again.');
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
    }
  };

  return (
    <div>
      <h2>Verify OTP</h2>
      <input
        type="email"
        placeholder="Enter email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button onClick={sendOtp}>Send OTP</button>

      <input
        type="text"
        placeholder="Enter OTP"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
      />
      <button onClick={handleVerifyOtp}>Verify OTP</button>
    </div>
  );
};

export default OtpPage;
