import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { sendOtp, verifyOtp } from '../services/authService';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false); // To track verification status
  const navigate = useNavigate();

  const handleSendOtp = async () => {
    if (!email) {
      setErrorMessage('Please enter a valid email.');
      return;
    }

    try {
      setErrorMessage(''); // Clear any previous error messages
      await sendOtp(email);
      setIsOtpSent(true); // OTP sent
      setOtp(''); // Clear the OTP input field
    } catch (error) {
      setErrorMessage('Failed to send OTP. Please try again later.');
    }
  };

  const handleVerifyOtp = async () => {
    if (!otp) {
      setErrorMessage('Please enter the OTP.');
      return;
    }

    setIsVerifying(true); // Set verifying state to true
    setErrorMessage(''); // Clear any previous error messages

    try {
      const { message, session } = await verifyOtp(email, otp); // Verify the OTP

      if (message === 'OTP verified successfully, user logged in.') {
        console.log('OTP verified successfully, user logged in', session);
        navigate('/home'); // After successful verification, redirect to home
      } else {
        setErrorMessage('Invalid OTP. Please try again.');
      }
    } catch (error) {
      setErrorMessage('Error during OTP verification. Please try again later.');
    } finally {
      setIsVerifying(false); // Reset verifying state after process
    }
  };

  return (
    <div className="login-page">
      <h2>Login</h2>

      <div className="input-group">
        <input
          type="email"
          placeholder="Enter email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button
          onClick={handleSendOtp}
          disabled={isOtpSent} // Disable the button after OTP is sent
        >
          {isOtpSent ? 'OTP Sent' : 'Send OTP'}
        </button>
      </div>

      {isOtpSent && (
        <div className="otp-section">
          <div className="input-group">
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>
          <button onClick={handleVerifyOtp} disabled={isVerifying}>
            {isVerifying ? 'Verifying...' : 'Verify OTP'}
          </button>
        </div>
      )}

      {/* Error message display */}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default LoginPage;
