import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; 
import './../../src/App.css';

const QuizPage = () => {
  const { id } = useParams(); // Get quiz ID from URL
  const navigate = useNavigate(); // Hook for navigation
  const [quiz, setQuiz] = useState(null); // Store quiz data
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track the current question index
  const [selectedAnswer, setSelectedAnswer] = useState(null); // Track selected answer for current question
  const [timer, setTimer] = useState(30); // Timer for each question (30 seconds)
  const [score, setScore] = useState(0); // Total score
  const [isAnswerCorrect, setIsAnswerCorrect] = useState(null); // Track if the selected answer was correct or not

  // Fetch quiz data on page load
  useEffect(() => {
    const fetchQuizData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/quiz/${id}`);
        if (!response.ok) {
          throw new Error('Quiz not found or error fetching quiz data.');
        }
        const data = await response.json();
        console.log('Fetched quiz data:', data);  // Log fetched data

        setQuiz(data); // Set quiz data
      } catch (error) {
        console.error('Error during fetch:', error);
        alert(error.message); // Show a meaningful error message
      }
    };

    fetchQuizData();
  }, [id]);

  // Memoize the checkAnswer function to prevent unnecessary re-creations
  const checkAnswer = useCallback((answerId) => {
    const question = quiz?.questions[currentQuestionIndex];
    const correctAnswer = question?.options.find((answer) => answer.is_correct);

    if (correctAnswer?.id === answerId) {
      setScore((prevScore) => prevScore + question.marks);
      setIsAnswerCorrect(true); // Correct answer
    } else {
      setIsAnswerCorrect(false); // Incorrect answer
    }
  }, [currentQuestionIndex, quiz]);

  // Handle the next question
  const handleNextQuestion = useCallback(() => {
    if (selectedAnswer !== null) {
      checkAnswer(selectedAnswer); // Check if the selected answer is correct
      setSelectedAnswer(null); // Reset selected answer
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1); // Move to the next question
      setTimer(30); // Reset timer for the next question
    }
  }, [selectedAnswer, checkAnswer]);

  // Countdown timer for each question
  useEffect(() => {
    if (timer > 0 && currentQuestionIndex < quiz?.questions.length) {
      const timerInterval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);
      }, 1000);
      return () => clearInterval(timerInterval);
    } else if (timer === 0) {
      handleNextQuestion(); // Move to the next question when the timer expires
    }
  }, [timer, currentQuestionIndex, quiz, handleNextQuestion]);

  // Submit the quiz to the server
  const submitQuiz = async () => {
    if (currentQuestionIndex !== quiz?.questions.length) {
      alert('Please complete the quiz before submitting.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/submit-quiz', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ quizId: id, answers: getAnswers() }),
      });
      const data = await response.json();
      console.log(data.message);

      // Navigate to the results page after submission
      alert(`Your score: ${score}`);
      navigate(`/quiz-results/${id}`); // Redirect to results page
    } catch (error) {
      console.error('Error submitting quiz:', error);
    }
  };

  // Get answers in the required format to send to the backend
  const getAnswers = () => {
    return quiz?.questions.map((question) => ({
      questionId: question.id,
      answerId: selectedAnswer,
    }));
  };

  if (!quiz) {
    return <div>Loading quiz...</div>; // Show loading while quiz data is being fetched
  }

  const currentQuestion = quiz?.questions[currentQuestionIndex];

  return (
    <div className="quiz-page">
      <h1>{quiz?.quiz_title}</h1>

      {/* Display Question and Timer */}
      <div className="question-container">
        <h3>{currentQuestion?.question_text}</h3>
        <div className="timer">Time Left: {timer}s</div>

        {/* Display the answer options */}
        <div className="answer-options">
          {currentQuestion?.options?.map((answer) => (
            <div
              key={answer.id}
              className={`answer-option ${selectedAnswer === answer.id ? 'selected' : ''}`}
              onClick={() => setSelectedAnswer(answer.id)} // Handle selecting an answer
            >
              {answer.text}
            </div>
          ))}
        </div>
      </div>

      {/* Next Button */}
      <div className="next-button">
        {currentQuestionIndex < quiz?.questions.length - 1 ? (
          <button onClick={handleNextQuestion} disabled={!selectedAnswer}>
            Next Question
          </button>
        ) : (
          <button onClick={submitQuiz}>Submit Quiz</button>
        )}
      </div>

      {/* Feedback for the selected answer */}
      {isAnswerCorrect !== null && (
        <div className="answer-feedback">
          {isAnswerCorrect ? 'Correct Answer!' : 'Incorrect Answer.'}
        </div>
      )}
    </div>
  );
};

export default QuizPage;
