import React, { useEffect, useState } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { getQuizDetails } from '../api';
import Question from '../components/Question';

const QuizPage = () => {
  const { id } = useParams();  // Get quiz ID from URL
  const [quiz, setQuiz] = useState(null);
  const [answers, setAnswers] = useState([]);
  const history = useHistory();

  useEffect(() => {
    async function fetchQuizDetails() {
      const data = await getQuizDetails(id);
      setQuiz(data);
    }
    fetchQuizDetails();
  }, [id]);

  const handleAnswer = (questionId, answer) => {
    setAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[questionId] = answer;
      return updatedAnswers;
    });
  };

  const handleSubmit = () => {
    const result = {
      userId: 1,  // Example, in reality you should get the logged-in user's ID
      quizId: quiz.id,
      score: answers.reduce((score, answer) => score + (answer.isCorrect ? 1 : 0), 0)
    };
    // Submit result to the backend
    submitQuizResult(result);
    history.push('/result');
  };

  if (!quiz) return <div>Loading...</div>;

  return (
    <div>
      <h1>{quiz.title}</h1>
      <div>
        {quiz.questions.map((question, index) => (
          <Question
            key={index}
            question={question}
            onAnswer={answer => handleAnswer(index, answer)}
          />
        ))}
      </div>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default QuizPage;
